import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './Profile.css';

const Profile = () => {
  const { id } = useParams(); // profile user ID
  const currentUserId = localStorage.getItem('userId');
  const isOwnProfile = id === currentUserId;
  const navigate = useNavigate();

  const [showDropdown, setShowDropdown] = useState(false);
  const [profile, setProfile] = useState({
    name: '',
    address: '',
    bio: '',
    profilePhoto: '/default-photo.png',
    stars: 4,
  });

  const toggleDropdown = () => setShowDropdown(!showDropdown);

  useEffect(() => {
    // TODO: Fetch user info using id
  }, [id]);

  const handleSignOut = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    navigate('/login');
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="dropdown-wrapper">
          <img src={profile.profilePhoto} alt="Profile" className="profile-pic" />
          {isOwnProfile && (
            <div className="dropdown-clickable">
              <button className="dropbtn" onClick={toggleDropdown}>⋮</button>
              {showDropdown && (
                <div className="dropdown-content show">
                  <a href={`/profile/${id}/change-password`}>Change Password</a>
                  <a href={`/profile/${id}/edit`}>Edit Profile</a>
                  <button className="dropdown-logout-btn" onClick={handleSignOut}>Sign Out</button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <div className="profile-main">
        <div className="profile-photo-box">
          <img src={profile.profilePhoto} alt="Profile" className="profile-photo" />
          <p>{profile.name}</p>
          <p>{profile.address}</p>
        </div>

        <div className="bio-box">
          <h2>Bio</h2>
          <p>{profile.bio}</p>
        </div>

        <div className="stars-box">
          <h3>Review Stars</h3>
          <p>{'★'.repeat(profile.stars)}{'☆'.repeat(5 - profile.stars)}</p>
        </div>
      </div>
    </div>
  );
};

export default Profile;
