import React, { useEffect, useState } from 'react';
import './Donations.css';

const mockDonations = [
  {
    id: 1,
    images: ['/images/sample1.jpg'],
    details: 'Leftover food from a wedding',
    locality: 'Downtown',
    expiresAt: new Date(Date.now() + 3600 * 1000), // 1 hour from now
    status: 'Available'
  },
  // Add 7 more objects with different IDs and dummy data
  // ...
];

const Donations = () => {
  const [donations, setDonations] = useState([]);

  useEffect(() => {
    const now = new Date();
    const filtered = mockDonations.filter(donation => new Date(donation.expiresAt) > now);
    setDonations(filtered);
  }, []);

  return (
    <div className="donations-container">
      {donations.map(donation => (
        <div key={donation.id} className="donation-card">
          <img src={donation.images[0]} alt="donation" className="donation-image" />
          <div className="donation-details">{donation.details}</div>
          <div className="booking-status">{donation.status}</div>
        </div>
      ))}
    </div>
  );
};

export default Donations;
