import React, { useState, useEffect } from 'react';
import './Donations.css';

const sampleDonations = [
  {
    id: 1,
    imageUrl: 'https://via.placeholder.com/200',
    donorName: 'John Doe',
    contact: 'john@example.com',
    address: '123 Elm St, Springfield',
    items: ['Bread', 'Milk', 'Vegetables'],
    datetime: '2025-05-25T12:00:00',
    accepted: false,
  },
  {
    id: 2,
    imageUrl: 'https://via.placeholder.com/200',
    donorName: 'Jane Smith',
    contact: 'jane@example.com',
    address: '456 Oak St, Metropolis',
    items: ['Canned Beans', 'Rice'],
    datetime: '2025-05-23T15:30:00',
    accepted: false,
  },
  // add more for 8 samples
];

const Donations = () => {
  const [donations, setDonations] = useState([]);

  useEffect(() => {
    const now = new Date();
    const validDonations = sampleDonations.filter(d => new Date(d.datetime) > now && !d.accepted);
    setDonations(validDonations);
  }, []);

  const handleAccept = (id) => {
    setDonations(prev => prev.filter(d => d.id !== id));
  };

  return (
    <div className="donations-container">
      {donations.map((donation) => (
        <div className="donation-card" key={donation.id}>
          <img src={donation.imageUrl} alt="Donation" className="donation-image" />
          <div className="donation-details">
            <p><strong>Donor:</strong> {donation.donorName}</p>
            <p><strong>Contact:</strong> {donation.contact}</p>
            <p><strong>Address:</strong> {donation.address}</p>
            <p><strong>Items:</strong> {donation.items.join(', ')}</p>
            <p><strong>Date & Time:</strong> {new Date(donation.datetime).toLocaleString()}</p>
            <button className="accept-btn" onClick={() => handleAccept(donation.id)}>Accept</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Donations;
